export default function Gallery({ pictures }) {
  // Nb image à afficher

  // Tableau pour chaque images
  const imageDivs = [];
  for (let i = 0; i < pictures.length; i++) {
    imageDivs.push(
      <div key={i}>
        <div style={{ backgroundImage: `url(${pictures[i]})` }}></div>
      </div>
    );
  }

  return <section id="slideshow">{imageDivs}</section>;
}
