import "../style/Collapse.css";
import { useState } from "react";
import DescriptionCollapse from "./DescriptionCollapse";
import { Fade } from "../components/Fade";
function Collapse({ id, title, description, visible, equipement }) {
  const [open, setOpen] = useState(true);
  const toggle = () => setOpen((o) => !o);
  let returnClassAngleDown = "fa-solid fa-angle-down";
  if (!visible) {
    returnClassAngleDown += " reverse";
  }

  return (
    <article key={id} className="collapseBar">
      <div className="titleBanner">
        <p className="titleCollapse">{title}</p>
        <i onClick={toggle} visible={open} className={returnClassAngleDown}></i>
      </div>
      <Fade visible={open}>
        <DescriptionCollapse description={description} />
      </Fade>
    </article>
  );
}

export default Collapse;
