function DescriptionCollapse({ description, equipements }) {
  if (description) {
    return (
      <>
        <p className="collapseDescription ">{description}</p>
      </>
    );
  } else if (equipements) {
    return (
      <>
        {equipements.map((equipement) => (
          <>
            <p className="collapseDescription" key={equipement}>
              {equipement}
            </p>
          </>
        ))}
      </>
    );
  }
}
export default DescriptionCollapse;
