import "../style/Collapse.css";

function clickArrowOpen() {
  const arrow = document.getElementsByClassName("fa-angle-down");
  console.log(arrow);
}

function Collapse({ id, title, description, equipement }) {
  clickArrowOpen();
  return (
    <article key={id} className="collapseBar">
      <div className="titleBanner">
        <p className="titleCollapse">{title}</p>
        <i className="fa-solid fa-angle-down"></i>
      </div>
      <p className="collapseDescription">
        {description}
        {equipement}
      </p>
    </article>
  );
}

export default Collapse;
