import { useState } from "react";
import { useParams } from "react-router-dom";
import datas from "../datas/datas.json";
export default function Gallery({ pictures }) {
  const [current, setCurrent] = useState(0);
  const length = pictures.length;
  const { id } = useParams();
  const rental = datas.find((i) => i.id === id);
  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };
  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };
  const imageDiv = [];
  const image = () => {
    for (let i = 0; i < length; i++) {
      return imageDiv.push(<img src={rental.pictures[i]()} alt="sd" />);
    }
  };
  if (!Array.isArray(pictures) || pictures.lenght <= 0) return null;
  return (
    <div className="Gallery">
      <i onClick={prevSlide} className="fa-solid fa-angle-left"></i>
      <i onClick={nextSlide} className="fa-solid fa-angle-right"></i>
      {image}
    </div>
  );
}
