import "../style/Collapse.css";
import { useState } from "react";
import DescriptionCollapse from "./DescriptionCollapse";

function Collapse({ id, title, description, equipement }) {
  const [close, setClose] = useState(false);
  const toggle = () => setClose((o) => !o);
  return (
    <article key={id} className="collapseBar">
      <div className="titleBanner">
        <p className="titleCollapse">{title}</p>
        <i onClick={toggle} className="fa-solid fa-angle-down reverse "></i>
      </div>
      <DescriptionCollapse visible={close} description={description} />
    </article>
  );
}

export default Collapse;
