function DescriptionCollapse({ description, equipement }) {
  if ({ description }) {
    return (
      <>
        <p className="collapseDescription ">{description}</p>
      </>
    );
  } else if ({ equipement }) {
    <>
      {equipement.map((equip) => (
        <>
          <p className="collapseDescription">{equip}</p>
          <br />
        </>
      ))}
    </>;
  }
}
export default DescriptionCollapse;
