import { useState } from "react";

export default function Gallery({ SliderData, slide }) {
  const [current, setCurrent] = useState(0);
  const length = slide.length;
  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };
  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };
  if (!Array.isArray(slide) || slide.lenght <= 0) return null;
  return (
    <>
      <div className="Gallery">
        <i onClick={prevSlide} className="fa-solid fa-angle-left"></i>
        <i onClick={nextSlide} className="fa-solid fa-angle-right"></i>
        {SliderData.map((slide, index) => {
          return (
            <div
              className={index === current ? "slide active" : "slide"}
              key={index}
            >
              {index === current && (
                <img src={slide.image} alt="travel image" className="image" />
              )}
            </div>
          );
        })}
      </div>
    </>
  );
}
