import Footer from "../components/Footer";
import Header from "../components/Header";
import imgHdp from "../assets/img_haut_de_page.png";
import "../style/Main.css";
import { Link } from "react-router-dom";
const annonces = require("../datas/datas.json");

function Home() {
  return (
    <>
      <Header />{" "}
      <main className="mainContainer">
        <figure>
          <img src={imgHdp} className="imgHdp" alt="banière haut de page" />
          <figcaption>Chez vous, partout et ailleurs</figcaption>
        </figure>
        <section className="annoncesContainer">
          {annonces.map((annonce) => (
            <Link key={annonce.id} to={`/annonces?${annonce.id}`}>
              <article className="cardAnnonce">
                <img src={annonce.cover} alt={annonce.title} />
                <h4>{annonce.title}</h4>
              </article>
            </Link>
          ))}
        </section>
      </main>{" "}
      <Footer />
    </>
  );
}

export default Home;
