import imgHdp from "../img_haut_de_page.png";
import "../style/Main.css";

const annonces = require("../datas/datas.json");

console.log(annonces);
function handleClick(annonceName) {
  alert(`Nique ta mère ${annonceName}`);
}

function Main() {
  return (
    <div className="mainContainer">
      <figure>
        <img src={imgHdp} className="imgHdp" />
        <figcaption>Chez vous, partout et ailleurs</figcaption>
      </figure>
      <section className="annoncesContainer">
        {annonces.map((annonce) => (
          <article
            onClick={() => handleClick(annonce.title)}
            key={annonce.id}
            className="cardAnnonce"
          >
            <img src={annonce.cover} />
            <h4>{annonce.title}</h4>
          </article>
        ))}
      </section>
    </div>
  );
}

export default Main;
