import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { fas } from "@awesome.me/kit-KIT_CODE/icons";
const angleDown = <FontAwesomeIcon icon={fas.faAngleDown} />;

function Collapse({ id, title, description, equipement }) {
  return (
    <article key={id} className="collapseBar">
      <div className="titleBanner">
        {angleDown}
        <p className="titleCollapse">{title}</p>
      </div>
      <p className="collapseDescription">
        {description}
        {equipement}
      </p>
    </article>
  );
}

export default Collapse;
