import { useState } from "react";
import { useParams } from "react-router-dom";
import datas from "../datas/datas.json";
export default function Gallery({ pictures }) {
  const [current, setCurrent] = useState(0);
  const { id } = useParams();
  const rental = datas.find((i) => i.id === id);
  const length = rental.pictures.length;
  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };
  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  return (
    <div className="Gallery">
      <i onClick={prevSlide} className="fa-solid fa-angle-left"></i>
      <i onClick={nextSlide} className="fa-solid fa-angle-right"></i>
      {rental.pictures.map((picture) => {
        return <img src={picture} alt="pic" key={picture} />;
      })}
    </div>
  );
}
